import logo from './logo.svg';
import './App.css';
import pokeapi from './pokeapi';
import { Component } from 'react';


class App extends React.Component{
  state = {
    pokemons:[],
  }
}

  fetch('https://pokeapi.co/api/v2/')
  .then(res => res.json())
  .then(res =>{
    this.setState({
      pokemons:res
    })
  })











export default App;
