import { func } from 'prop-types';
import React, { useState } from 'react';
import PokemonCapturado from '../../Pokemon';
let getMypokemonOnStorage =[];

localStorage.clear();

function Card({pokemon}){
    pokemon.available=true;
    console.log(JSON.parse(localStorage.getItem('pokemon')));

  const click = function getpokes() {
    getMypokemonOnStorage.push(localStorage.getItem('pokemon'));
   
    if(pokemon.available===true){
        PokemonCapturado.available=false;
        PokemonCapturado.push(pokemon);
             //console.log(PokemonCapturado);
          
             localStorage.setItem('pokemon',JSON.stringify(PokemonCapturado));
             PokemonCapturado.push(JSON.parse(getMypokemonOnStorage));
             console.log(JSON.parse(getMypokemonOnStorage));
    }
      
    }



    return(
        

        
        <div className="Card" key={pokemon.id}>
            <hr/>
     <button id={pokemon.id} onClick={click}>Capturar {pokemon.id} </button>
             
             <h1> {pokemon.id}</h1>  
            <div className="Card_img">
             <img src={pokemon.sprites.front_default} alt=""></img>
            </div>
            <div className="Card_name">
           
               <h1> {pokemon.name}</h1> 
            </div>
            <div className="Card_types">
             <h1>
                 
             </h1>
                {pokemon.types.map(type =>{
                    return (
                     <div className="Card_type" key={type.type.name}>
                        <h3> {type.type.name} </h3> 

                     </div>
                    );
                })}
            </div>
            <div  className="Card_info" key={pokemon.weight}>
                <div className="Card_Dados_weight">
                    <p className="title">Weight</p>
                    <p>{pokemon.weight}</p>
                 
                </div>
                <div className="Card_Dados_height" key={pokemon.height}>
                    <p className="title">Height</p>
                    <p>{pokemon.height}</p>
                 
                </div>
                <div className="Card_Dados_ability"key={pokemon.abilities[0].ability.name} >
                    <p className="title">Ability</p>
                    <p>{pokemon.abilities[0].ability.name}</p>
                    
                 
                </div>
              
            </div>
           
        </div>
    )
}
export default Card;
